function viewImage(){this.parentNode.classList.add(activeClass)}function hideImage(){this.parentNode.classList.remove(activeClass)}for(var activeClass="active",viewBtns=document.querySelectorAll(".view"),closeBtns=document.querySelectorAll(".close"),i=0;i<viewBtns.length;i++)viewBtns[i].addEventListener("click",viewImage);for(var j=0;j<closeBtns.length;j++)closeBtns[j].addEventListener("click",hideImage);